import { Link, useParams } from 'react-router-dom'

const ProductDetailPage = () => {
  const params = useParams()

  return (
    <div>
      <h1>Product Detail Page</h1>
      <p>{params.productId}</p>
      <Link to=".." relative='path'></Link>
    </div>
  )
}

export default ProductDetailPage
